      styles: ["\n\n.shipment-container[_ngcontent-%COMP%] {\n  max-width: 1200px;\n  margin: 0 auto;\n  padding: 2rem;\n  background: #f8f9fa;\n  min-height: 100vh;\n  margin-top: 80px;\n}\n\n\n\n.shipment-header[_ngcontent-%COMP%] {\n  background: white;\n  border-radius: 15px;\n  padding: 2rem;\n  margin-bottom: 2rem;\n  box-shadow: 0 4px 15px rgba(0, 0, 0, 0.1);\n}\n\n.header-content[_ngcontent-%COMP%] {\n  text-align: center;\n  margin-bottom: 2rem;\n}\n\n.page-title[_ngcontent-%COMP%] {\n  font-size: 2.5rem;\n  font-weight: 700;\n  color: #333;\n  margin-bottom: 0.5rem;\n  display: flex;\n  align-items: center;\n  justify-content: center;\n  gap: 1rem;\n}\n\n.title-icon[_ngcontent-%COMP%] {\n  font-size: 2.5rem;\n}\n\n.page-subtitle[_ngcontent-%COMP%] {\n  font-size: 1.1rem;\n  color: #666;\n  margin: 0;\n}\n\n\n\n.progress-container[_ngcontent-%COMP%] {\n  max-width: 800px;\n  margin: 0 auto;\n}\n\n.progress-bar[_ngcontent-%COMP%] {\n  width: 100%;\n  height: 8px;\n  background: #e9ecef;\n  border-radius: 10px;\n  margin-bottom: 2rem;\n  overflow: hidden;\n}\n\n.progress-fill[_ngcontent-%COMP%] {\n  height: 100%;\n  background: linear-gradient(45deg, #007bff, #0056b3);\n  border-radius: 10px;\n  transition: width 0.3s ease;\n}\n\n.steps-indicator[_ngcontent-%COMP%] {\n  display: flex;\n  justify-content: space-between;\n  align-items: center;\n}\n\n.step-indicator[_ngcontent-%COMP%] {\n  display: flex;\n  flex-direction: column;\n  align-items: center;\n  cursor: pointer;\n  transition: all 0.3s ease;\n  flex: 1;\n  max-width: 150px;\n}\n\n.step-indicator[_ngcontent-%COMP%]:hover {\n  transform: translateY(-2px);\n}\n\n.step-number[_ngcontent-%COMP%] {\n  width: 40px;\n  height: 40px;\n  border-radius: 50%;\n  background: #e9ecef;\n  color: #666;\n  display: flex;\n  align-items: center;\n  justify-content: center;\n  font-weight: bold;\n  margin-bottom: 0.5rem;\n  transition: all 0.3s ease;\n}\n\n.step-indicator.active[_ngcontent-%COMP%]   .step-number[_ngco