import { Component, OnInit, OnDestroy } from '@angular/core';
import { Router } from '@angular/router';
import { Subscription } from 'rxjs';
import { HttpClient, HttpErrorResponse } from '@angular/common/http';
import { AuthService } from '../../services/auth.service';
import { EnviosService } from '../../services/envios.service';

interface User {
  id: string;
  username: string;
  name: string;
  role: string;
  email: string;
}

// Interface para direcciones frecuentes
interface FrequentAddress {
  id: string;
  name: string;
  email: string;
  phone: string;
  reference: string;
  department: string;
  municipality: string;
  poblado: string;
  colonia: string;
  exactAddress: string;
  contactName: string;
  specialInstructions?: string;
  createdBy: string;
  usageCount: number;
}

// Interface para el formulario de envío renovado
interface NewShipmentForm {
  // Paso 1: Dirección del destinatario
  destinatario: {
    nombre: string;           // *
    email: string;           // *
    referencia: string;      // * casa, trabajo, gimnasio, escuela
    poblado: string;
    municipio: string;
    departamento: string;    // *
    colonia: string;
    residenciaBarrio: string;
    telefono: string;        // *
    instruccionesEspeciales?: string;
    direccionExacta: string; // *
    nombreContacto: string;
  };
  
  // Paso 2: Método de pago
  metodoPago: {
    tipo: string; // 'contra_entrega' | 'mi_cuenta' | 'tarjeta'
    cargoAdicional: number;
  };
  
  // Paso 3: Tipo de paquete
  paquete: {
    tipo: string;
    peso: number;
    dimensiones: {
      largo: number;
      ancho: number;
      alto: number;
    };
    descripcion: string;
    valorDeclarado: number;
    fragil: boolean;
  };
  
  // Paso 4: Resumen y confirmación
  costoTotal: number;
  codigoGuia: string;
}

// Interface para departamentos y municipios
interface LocationData {
  departamento: string;
  municipios: string[];
  poblados?: { [municipio: string]: string[] };
}
    origin: {
      department: string;
      municipality: string;
      address: string;
      zone: string;
      reference: string;
    };
    destination: {
      department: string;
      municipality: string;
      address: string;
      zone: string;
      reference: string;
    };
  };
  
  // Configuración del envío
  service_type: string;
  priority: string;
  delivery_date: string;
  notes: string;
}

interface ShipmentResponse {
  success: boolean;
  message: string;
  shipment?: {
    shipment_id: string;
    tracking_url: string;
  };
}

@Component({
  selector: 'app-realizar-envio',
  templateUrl: './realizar-envio.component.html',
  styleUrls: ['./realizar-envio.component.css']
})
export class RealizarEnvioComponent implements OnInit, OnDestroy {
  private subscription: Subscription = new Subscription();
  private apiUrl = 'http://localhost:3005/api';
  
  currentUser: User | null = null;
  isLoading = false;
  isSubmitting = false;
  errorMessage = '';
  successMessage = '';
  currentStep = 1;
  totalSteps = 4;
  
  shipmentForm: ShipmentForm = {
    sender: {
      name: '',
      phone: '',
      email: '',
      document_type: 'DPI',
      document_number: ''
    },
    receiver: {
      name: '',
      phone: '',
      email: '',
      document_type: 'DPI',
      document_number: ''
    },
    package_details: {
      description: '',
      weight: 0,
      dimensions: {
        length: 0,
        width: 0,
        height: 0
      },
      declared_value: 0,
      fragile: false,
      special_handling: ''
    },
    addresses: {
      origin: {
        department: '',
        municipality: '',
        address: '',
        zone: '',
        reference: ''
      },
      destination: {
        department: '',
        municipality: '',
        address: '',
        zone: '',
        reference: ''
      }
    },
    service_type: 'standard',
    priority: 'normal',
    delivery_date: '',
    notes: ''
  };
  
  // Opciones para selects
  documentTypes = [
    { value: 'DPI', label: 'DPI' },
    { value: 'NIT', label: 'NIT' },
    { value: 'Passport', label: 'Pasaporte' }
  ];
  
  serviceTypes = [
    { value: 'standard', label: 'Estándar (3-5 días)', price: 'Base' },
    { value: 'express', label: 'Express (1-2 días)', price: '+50%' },
    { value: 'overnight', label: 'Overnight (24 horas)', price: '+100%' }
  ];
  
  priorities = [
    { value: 'low', label: 'Baja', color: '#28a745' },
    { value: 'normal', label: 'Normal', color: '#007bff' },
    { value: 'high', label: 'Alta', color: '#ffc107' },
    { value: 'urgent', label: 'Urgente', color: '#dc3545' }
  ];
  
  // Departamentos de Guatemala
  departments = [
    'Alta Verapaz', 'Baja Verapaz', 'Chimaltenango', 'Chiquimula', 'Petén',
    'El Progreso', 'Quiché', 'Escuintla', 'Guatemala', 'Huehuetenango',
    'Izabal', 'Jalapa', 'Jutiapa', 'Quetzaltenango', 'Retalhuleu',
    'Sacatepéquez', 'San Marcos', 'Santa Rosa', 'Sololá', 'Suchitepéquez',
    'Totonicapán', 'Zacapa'
  ];

  constructor(
    private authService: AuthService,
    private router: Router,
    private http: HttpClient,
    private enviosService: EnviosService
  ) {}

  ngOnInit(): void {
    this.loadUserData();
    this.setMinDeliveryDate();
  }

  ngOnDestroy(): void {
    this.subscription.unsubscribe();
  }

  private loadUserData(): void {
    this.subscription.add(
      this.authService.currentUser$.subscribe(user => {
        this.currentUser = user;
        if (user) {
          // Pre-llenar datos del usuario actual como remitente por defecto
          this.prefillSenderData(user);
        }
      })
    );
  }

  private prefillSenderData(user: User): void {
    this.shipmentForm.sender.name = user.name;
    this.shipmentForm.sender.email = user.email;
  }

  private setMinDeliveryDate(): void {
    const tomorrow = new Date();
    tomorrow.setDate(tomorrow.getDate() + 1);
    this.shipmentForm.delivery_date = tomorrow.toISOString().split('T')[0];
  }

  // Navegación entre pasos
  nextStep(): void {
    if (this.validateCurrentStep()) {
      if (this.currentStep < this.totalSteps) {
        this.currentStep++;
        this.clearMessages();
      }
    }
  }

  prevStep(): void {
    if (this.currentStep > 1) {
      this.currentStep--;
      this.clearMessages();
    }
  }

  goToStep(step: number): void {
    if (step >= 1 && step <= this.totalSteps) {
      this.currentStep = step;
      this.clearMessages();
    }
  }

  // Validaciones
  validateCurrentStep(): boolean {
    this.clearMessages();
    
    switch (this.currentStep) {
      case 1:
        return this.validateSenderReceiver();
      case 2:
        return this.validatePackageDetails();
      case 3:
        return this.validateAddresses();
      case 4:
        return this.validateServiceOptions();
      default:
        return false;
    }
  }

  private validateSenderReceiver(): boolean {
    const { sender, receiver } = this.shipmentForm;
    
    if (!sender.name.trim()) {
      this.errorMessage = 'El nombre del remitente es requerido';
      return false;
    }
    
    if (!sender.phone.trim()) {
      this.errorMessage = 'El teléfono del remitente es requerido';
      return false;
    }
    
    if (!sender.document_number.trim()) {
      this.errorMessage = 'El número de documento del remitente es requerido';
      return false;
    }
    
    if (!receiver.name.trim()) {
      this.errorMessage = 'El nombre del destinatario es requerido';
      return false;
    }
    
    if (!receiver.phone.trim()) {
      this.errorMessage = 'El teléfono del destinatario es requerido';
      return false;
    }
    
    if (!receiver.document_number.trim()) {
      this.errorMessage = 'El número de documento del destinatario es requerido';
      return false;
    }
    
    return true;
  }

  private validatePackageDetails(): boolean {
    const { package_details } = this.shipmentForm;
    
    if (!package_details.description.trim()) {
      this.errorMessage = 'La descripción del paquete es requerida';
      return false;
    }
    
    if (package_details.weight <= 0) {
      this.errorMessage = 'El peso debe ser mayor a 0';
      return false;
    }
    
    if (package_details.dimensions.length <= 0 || 
        package_details.dimensions.width <= 0 || 
        package_details.dimensions.height <= 0) {
      this.errorMessage = 'Todas las dimensiones deben ser mayores a 0';
      return false;
    }
    
    if (package_details.declared_value <= 0) {
      this.errorMessage = 'El valor declarado debe ser mayor a 0';
      return false;
    }
    
    return true;
  }

  private validateAddresses(): boolean {
    const { origin, destination } = this.shipmentForm.addresses;
    
    if (!origin.department || !origin.municipality || !origin.address.trim()) {
      this.errorMessage = 'Todos los campos de dirección de origen son requeridos';
      return false;
    }
    
    if (!destination.department || !destination.municipality || !destination.address.trim()) {
      this.errorMessage = 'Todos los campos de dirección de destino son requeridos';
      return false;
    }
    
    return true;
  }

  private validateServiceOptions(): boolean {
    if (!this.shipmentForm.service_type) {
      this.errorMessage = 'Debe seleccionar un tipo de servicio';
      return false;
    }
    
    if (!this.shipmentForm.priority) {
      this.errorMessage = 'Debe seleccionar una prioridad';
      return false;
    }
    
    if (!this.shipmentForm.delivery_date) {
      this.errorMessage = 'Debe seleccionar una fecha de entrega';
      return false;
    }
    
    return true;
  }

  // Envío del formulario
  onSubmit(): void {
    if (!this.validateCurrentStep()) {
      return;
    }
    
    this.isSubmitting = true;
    this.clearMessages();
    
    // Preparar datos para el nuevo endpoint mejorado
    const shipmentData = {
      // Datos del remitente
      senderName: this.shipmentForm.sender.name,
      senderPhone: this.shipmentForm.sender.phone,
      senderEmail: this.shipmentForm.sender.email,
      
      // Datos del destinatario
      receiverName: this.shipmentForm.receiver.name,
      receiverPhone: this.shipmentForm.receiver.phone,
      receiverEmail: this.shipmentForm.receiver.email,
      
      // Detalles del paquete
      packageWeight: this.shipmentForm.package_details.weight,
      packageDimensions: {
        length: this.shipmentForm.package_details.dimensions.length,
        width: this.shipmentForm.package_details.dimensions.width,
        height: this.shipmentForm.package_details.dimensions.height
      },
      packageDescription: this.shipmentForm.package_details.description,
      packageValue: this.shipmentForm.package_details.declared_value,
      fragile: this.shipmentForm.package_details.fragile,
      
      // Direcciones
      senderAddress: {
        department: this.shipmentForm.addresses.origin.department,
        municipality: this.shipmentForm.addresses.origin.municipality,
        zone: this.shipmentForm.addresses.origin.zone,
        address: this.shipmentForm.addresses.origin.address,
        reference: this.shipmentForm.addresses.origin.reference
      },
      receiverAddress: {
        department: this.shipmentForm.addresses.destination.department,
        municipality: this.shipmentForm.addresses.destination.municipality,
        zone: this.shipmentForm.addresses.destination.zone,
        address: this.shipmentForm.addresses.destination.address,
        reference: this.shipmentForm.addresses.destination.reference
      },
      
      // Configuración del servicio
      serviceType: this.shipmentForm.service_type,
      paymentMethod: 'cash', // Por defecto efectivo
      additionalServices: [],
      notes: this.shipmentForm.notes
    };
    
    console.log('📦 Enviando datos del formulario:', shipmentData);
    
    this.subscription.add(
      this.enviosService.crearEnvioMejorado(shipmentData).subscribe({
        next: (response: any) => {
          this.isSubmitting = false;
          
          if (response.success) {
            this.successMessage = `¡Envío creado exitosamente! 
                                 Número de seguimiento: ${response.data.trackingNumber}
                                 Precio estimado: Q${response.data.estimatedPrice}`;
            
            // Esperar 3 segundos y redirigir
            setTimeout(() => {
              this.router.navigate(['/rastreo'], { 
                queryParams: { tracking: response.data.trackingNumber } 
              });
            }, 3000);
          } else {
            this.errorMessage = response.message || 'Error al crear el envío';
          }
        },
        error: (error: HttpErrorResponse) => {
          this.isSubmitting = false;
          
          if (error.status === 401) {
            this.errorMessage = 'No tienes permisos para crear envíos. Por favor inicia sesión nuevamente.';
            this.router.navigate(['/login']);
          } else if (error.status === 400) {
            this.errorMessage = error.error?.message || 'Datos del formulario inválidos';
          } else {
            this.errorMessage = 'Error interno del servidor. Intenta nuevamente.';
          }
          
          console.error('❌ Error al crear envío:', error);
        }
      })
    );
  }

  // Utilidades
  private clearMessages(): void {
    this.errorMessage = '';
    this.successMessage = '';
  }

  resetForm(): void {
    this.currentStep = 1;
    this.shipmentForm = {
      sender: { name: '', phone: '', email: '', document_type: 'DPI', document_number: '' },
      receiver: { name: '', phone: '', email: '', document_type: 'DPI', document_number: '' },
      package_details: {
        description: '', weight: 0, dimensions: { length: 0, width: 0, height: 0 },
        declared_value: 0, fragile: false, special_handling: ''
      },
      addresses: {
        origin: { department: '', municipality: '', address: '', zone: '', reference: '' },
        destination: { department: '', municipality: '', address: '', zone: '', reference: '' }
      },
      service_type: 'standard', priority: 'normal', delivery_date: '', notes: ''
    };
    
    if (this.currentUser) {
      this.prefillSenderData(this.currentUser);
    }
    this.setMinDeliveryDate();
    this.clearMessages();
  }

  getStepTitle(step: number): string {
    const titles = {
      1: 'Datos Personales',
      2: 'Detalles del Paquete',
      3: 'Direcciones',
      4: 'Configuración del Envío'
    };
    return titles[step as keyof typeof titles] || '';
  }

  isStepValid(step: number): boolean {
    const currentStepBackup = this.currentStep;
    this.currentStep = step;
    const isValid = this.validateCurrentStep();
    this.currentStep = currentStepBackup;
    return isValid;
  }

  // Métodos auxiliares para el template
  getServiceTypeLabel(): string {
    const service = this.serviceTypes.find(s => s.value === this.shipmentForm.service_type);
    return service ? service.label : '';
  }

  getPriorityLabel(): string {
    const priority = this.priorities.find(p => p.value === this.shipmentForm.priority);
    return priority ? priority.label : '';
  }

  getDepartmentDisplay(department: string): string {
    return department || 'No seleccionado';
  }

  getMinDate(): string {
    const today = new Date();
    return today.toISOString().split('T')[0];
  }
}
