    } else {
      this.destinoFiltrado = this.todosLosMunicipios.filter(m => m.nombre.toLowerCase().includes(busqueda) || m.departamento.toLowerCase().includes(busqueda)).map(m => `${m.nombre}, ${m.departamento}`);
