import { Component, OnInit, OnDestroy } from '@angular/core';
import { Router } from '@angular/router';
import { Subscription } from 'rxjs';
import { HttpClient, HttpErrorResponse } from '@angular/common/http';
import { AuthService } from '../../services/auth.service';
import { EnviosService } from '../../services/envios.service';

interface User {
  id: string;
  username: string;
  name: string;
  role: string;
  email: string;
}

// Interface para direcciones frecuentes
interface FrequentAddress {
  id: string;
  name: string;
  email: string;
  phone: string;
  reference: string;
  department: string;
  municipality: string;
  poblado: string;
  colonia: string;
  exactAddress: string;
  contactName: string;
  specialInstructions?: string;
  createdBy: string;
  usageCount: number;
}

// Interface para el formulario de envío renovado
interface NewShipmentForm {
  // Paso 1: Dirección del destinatario
  destinatario: {
    nombre: string;           // *
    email: string;           // *
    referencia: string;      // * casa, trabajo, gimnasio, escuela
    poblado: string;
    municipio: string;
    departamento: string;    // *
    colonia: string;
    residenciaBarrio: string;
    telefono: string;        // *
    instruccionesEspeciales?: string;
    direccionExacta: string; // *
    nombreContacto: string;
  };
  
  // Paso 2: Método de pago
  metodoPago: {
    tipo: string; // 'contra_entrega' | 'mi_cuenta' | 'tarjeta'
    cargoAdicional: number;
  };
  
  // Paso 3: Tipo de paquete
  paquete: {
    tipo: string;
    peso: number;
    dimensiones: {
      largo: number;
      ancho: number;
      alto: number;
    };
    descripcion: string;
    valorDeclarado: number;
    fragil: boolean;
  };
  
  // Paso 4: Resumen y confirmación
  costoTotal: number;
  codigoGuia: string;
}

// Interface para departamentos y municipios
interface LocationData {
  departamento: string;
  municipios: string[];
  poblados?: { [municipio: string]: string[] };
}
@Component({
  selector: 'app-realizar-envio',
  templateUrl: './realizar-envio.component.html',
  styleUrls: ['./realizar-envio.component.css']
})
export class RealizarEnvioComponent implements OnInit, OnDestroy {
  private subscription: Subscription = new Subscription();
  private apiUrl = 'http://localhost:3005/api';
  
  currentUser: User | null = null;
  isLoading = false;
  isSubmitting = false;
  errorMessage = '';
  successMessage = '';
  currentStep = 1;
  totalSteps = 4;

  // Datos para la búsqueda de direcciones frecuentes
  searchTerm = '';
  frequentAddresses: FrequentAddress[] = [];
  showFrequentAddresses = false;
  
  // Formulario principal
  shipmentForm: NewShipmentForm = {
    destinatario: {
      nombre: '',
      email: '',
      referencia: '',
      poblado: '',
      municipio: '',
      departamento: '',
      colonia: '',
      residenciaBarrio: '',
      telefono: '',
      instruccionesEspeciales: '',
      direccionExacta: '',
      nombreContacto: ''
    },
    metodoPago: {
      tipo: 'contra_entrega',
      cargoAdicional: 4.00
    },
    paquete: {
      tipo: '',
      peso: 0,
      dimensiones: {
        largo: 0,
        ancho: 0,
        alto: 0
      },
      descripcion: '',
      valorDeclarado: 0,
      fragil: false
    },
    costoTotal: 0,
    codigoGuia: ''
  };
  
  // Opciones para los selectores
  referenciaOpciones = [
    { value: 'casa', label: 'Casa' },
    { value: 'trabajo', label: 'Trabajo' },
    { value: 'gimnasio', label: 'Gimnasio' },
    { value: 'escuela', label: 'Escuela' }
  ];
  
  metodoPagoOpciones = [
    { 
      value: 'contra_entrega', 
      label: 'Cobro contra entrega', 
      cargo: 4.00,
      descripcion: 'Q. 4.00 de cargos adicionales' 
    },
    { 
      value: 'mi_cuenta', 
      label: 'Cobro a mi cuenta', 
      cargo: 0,
      descripcion: 'Sin cargos adicionales' 
    },
    { 
      value: 'tarjeta', 
      label: 'Pago con tarjeta de crédito o débito', 
      cargo: 0,
      descripcion: 'Pago inmediato' 
    }
  ];
  
  tiposPaquete = [
    { value: 'sobres', label: 'Sobres (hasta 500g)', maxPeso: 0.5, precio: 15 },
    { value: 'paquete_pequeno', label: 'Paquete Pequeño (hasta 2kg)', maxPeso: 2, precio: 25 },
    { value: 'paquete_mediano', label: 'Paquete Mediano (hasta 5kg)', maxPeso: 5, precio: 35 },
    { value: 'paquete_grande', label: 'Paquete Grande (hasta 10kg)', maxPeso: 10, precio: 50 },
    { value: 'paquete_extra', label: 'Paquete Extra Grande (hasta 25kg)', maxPeso: 25, precio: 75 }
  ];
  
  // Datos de ubicaciones (como en cotizar envíos)
  departamentos: LocationData[] = [
    {
      departamento: 'Guatemala',
      municipios: ['Guatemala', 'Mixco', 'Villa Nueva', 'San José Pinula', 'Santa Catarina Pinula'],
      poblados: {
        'Guatemala': ['Zona 1', 'Zona 4', 'Zona 9', 'Zona 10', 'Zona 11', 'Zona 13', 'Zona 14', 'Zona 15'],
        'Mixco': ['Zona 1 de Mixco', 'Zona 3 de Mixco', 'Zona 6 de Mixco', 'Las Brisas', 'El Mirador']
      }
    },
    {
      departamento: 'Sacatepéquez',
      municipios: ['Antigua Guatemala', 'Chimaltenango', 'San Lucas Sacatepéquez'],
      poblados: {
        'Antigua Guatemala': ['Centro Histórico', 'San Pedro Las Huertas', 'Santa Ana'],
        'San Lucas Sacatepéquez': ['Centro', 'Carretera a El Salvador']
      }
    }
    // Agregar más departamentos según sea necesario
  ];
  
  // Variables para los selectores dependientes
  municipiosDisponibles: string[] = [];
  pobladosDisponibles: string[] = [];

  constructor(
    private authService: AuthService,
    private router: Router,
    private http: HttpClient,
    private enviosService: EnviosService
  ) {}

  ngOnInit(): void {
    this.loadUserData();
    this.loadFrequentAddresses();
  }

  ngOnDestroy(): void {
    this.subscription.unsubscribe();
  }

  private loadUserData(): void {
    this.subscription.add(
      this.authService.currentUser$.subscribe(user => {
        this.currentUser = user;
      })
    );
  }

  // Cargar direcciones frecuentes del usuario
  private loadFrequentAddresses(): void {
    this.isLoading = true;
    this.subscription.add(
      this.http.get<{success: boolean, data: FrequentAddress[]}>(`${this.apiUrl}/frequent-addresses`).subscribe({
        next: (response) => {
          if (response.success) {
            this.frequentAddresses = response.data;
          }
          this.isLoading = false;
        },
        error: (error) => {
          console.error('Error loading frequent addresses:', error);
          this.isLoading = false;
        }
      })
    );
  }

  // Buscar en direcciones frecuentes
  onSearchFrequentAddresses(): void {
    if (this.searchTerm.length >= 2) {
      this.showFrequentAddresses = true;
    } else {
      this.showFrequentAddresses = false;
    }
  }

  // Filtrar direcciones frecuentes
  getFilteredFrequentAddresses(): FrequentAddress[] {
    if (!this.searchTerm) return this.frequentAddresses;
    
    const term = this.searchTerm.toLowerCase();
    return this.frequentAddresses.filter(addr => 
      addr.name.toLowerCase().includes(term) ||
      addr.department.toLowerCase().includes(term) ||
      addr.municipality.toLowerCase().includes(term) ||
      addr.exactAddress.toLowerCase().includes(term)
    );
  }

  // Seleccionar dirección frecuente
  selectFrequentAddress(address: FrequentAddress): void {
    this.shipmentForm.destinatario = {
      nombre: address.name,
      email: address.email,
      referencia: address.reference,
      poblado: address.poblado,
      municipio: address.municipality,
      departamento: address.department,
      colonia: address.colonia,
      residenciaBarrio: '',
      telefono: address.phone,
      instruccionesEspeciales: address.specialInstructions || '',
      direccionExacta: address.exactAddress,
      nombreContacto: address.contactName
    };
    
    this.updateMunicipios();
    this.updatePoblados();
    this.showFrequentAddresses = false;
    this.searchTerm = '';
    
    // Incrementar contador de uso
    this.incrementAddressUsage(address.id);
  }

  private incrementAddressUsage(addressId: string): void {
    this.http.patch(`${this.apiUrl}/frequent-addresses/${addressId}/increment`, {}).subscribe();
  }

  // Manejar cambios en departamento
  onDepartamentoChange(): void {
    this.shipmentForm.destinatario.municipio = '';
    this.shipmentForm.destinatario.poblado = '';
    this.updateMunicipios();
    this.pobladosDisponibles = [];
  }

  // Manejar cambios en municipio
  onMunicipioChange(): void {
    this.shipmentForm.destinatario.poblado = '';
    this.updatePoblados();
  }

  private updateMunicipios(): void {
    const dept = this.departamentos.find(d => d.departamento === this.shipmentForm.destinatario.departamento);
    this.municipiosDisponibles = dept ? dept.municipios : [];
  }

  private updatePoblados(): void {
    const dept = this.departamentos.find(d => d.departamento === this.shipmentForm.destinatario.departamento);
    if (dept && dept.poblados) {
      this.pobladosDisponibles = dept.poblados[this.shipmentForm.destinatario.municipio] || [];
    } else {
      this.pobladosDisponibles = [];
    }
  }

  // Manejar cambio de método de pago
  onMetodoPagoChange(): void {
    const metodo = this.metodoPagoOpciones.find(m => m.value === this.shipmentForm.metodoPago.tipo);
    this.shipmentForm.metodoPago.cargoAdicional = metodo ? metodo.cargo : 0;
    this.calcularCostoTotal();
  }

  // Manejar cambio de tipo de paquete
  onTipoPaqueteChange(): void {
    const tipo = this.tiposPaquete.find(t => t.value === this.shipmentForm.paquete.tipo);
    if (tipo && this.shipmentForm.paquete.peso > tipo.maxPeso) {
      this.shipmentForm.paquete.peso = tipo.maxPeso;
    }
    this.calcularCostoTotal();
  }

  // Calcular costo total del envío
  calcularCostoTotal(): void {
    let costoBase = 0;
    
    // Obtener precio base según tipo de paquete
    const tipoPaquete = this.tiposPaquete.find(t => t.value === this.shipmentForm.paquete.tipo);
    if (tipoPaquete) {
      costoBase = tipoPaquete.precio;
    }
    
    // Agregar cargos adicionales por método de pago
    const cargosPago = this.shipmentForm.metodoPago.cargoAdicional;
    
    // Calcular recargo por peso extra si aplica
    let recargosPeso = 0;
    if (tipoPaquete && this.shipmentForm.paquete.peso > tipoPaquete.maxPeso) {
      const pesoExtra = this.shipmentForm.paquete.peso - tipoPaquete.maxPeso;
      recargosPeso = pesoExtra * 5; // Q5 por kg extra
    }
    
    this.shipmentForm.costoTotal = costoBase + cargosPago + recargosPeso;
  }

  // Navegación entre pasos
  nextStep(): void {
    if (this.validateCurrentStep()) {
      if (this.currentStep < this.totalSteps) {
        this.currentStep++;
        if (this.currentStep === 4) {
          this.calcularCostoTotal();
          this.generateGuiaCode();
        }
        this.clearMessages();
      }
    }
  }

  prevStep(): void {
    if (this.currentStep > 1) {
      this.currentStep--;
      this.clearMessages();
    }
  }

  goToStep(step: number): void {
    if (step >= 1 && step <= this.totalSteps) {
      this.currentStep = step;
      this.clearMessages();
    }
  }

  // Validaciones por paso
  validateCurrentStep(): boolean {
    this.clearMessages();
    
    switch (this.currentStep) {
      case 1:
        return this.validateDestinatario();
      case 2:
        return this.validateMetodoPago();
      case 3:
        return this.validatePaquete();
      case 4:
        return true; // Resumen, no necesita validación adicional
      default:
        return false;
    }
  }

  private validateDestinatario(): boolean {
    const dest = this.shipmentForm.destinatario;
    
    if (!dest.nombre.trim()) {
      this.errorMessage = 'El nombre es obligatorio';
      return false;
    }
    
    if (!dest.email.trim() || !this.isValidEmail(dest.email)) {
      this.errorMessage = 'El correo electrónico es obligatorio y debe ser válido';
      return false;
    }
    
    if (!dest.referencia) {
      this.errorMessage = 'La referencia es obligatoria';
      return false;
    }
    
    if (!dest.departamento) {
      this.errorMessage = 'El departamento es obligatorio';
      return false;
    }
    
    if (!dest.telefono.trim()) {
      this.errorMessage = 'El número de teléfono es obligatorio';
      return false;
    }
    
    if (!dest.direccionExacta.trim()) {
      this.errorMessage = 'La dirección exacta es obligatoria';
      return false;
    }
    
    return true;
  }

  private validateMetodoPago(): boolean {
    if (!this.shipmentForm.metodoPago.tipo) {
      this.errorMessage = 'Debe seleccionar un método de pago';
      return false;
    }
    return true;
  }

  private validatePaquete(): boolean {
    const paq = this.shipmentForm.paquete;
    
    if (!paq.tipo) {
      this.errorMessage = 'Debe seleccionar un tipo de paquete';
      return false;
    }
    
    if (paq.peso <= 0) {
      this.errorMessage = 'El peso debe ser mayor a 0';
      return false;
    }
    
    if (!paq.descripcion.trim()) {
      this.errorMessage = 'La descripción del paquete es obligatoria';
      return false;
    }
    
    if (paq.valorDeclarado <= 0) {
      this.errorMessage = 'El valor declarado debe ser mayor a 0';
      return false;
    }
    
    return true;
  }

  private isValidEmail(email: string): boolean {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email);
  }

  // Generar código de guía único
  private generateGuiaCode(): void {
    const prefix = 'DSE';
    const timestamp = Date.now().toString().slice(-6);
    const random = Math.random().toString(36).substr(2, 4).toUpperCase();
    this.shipmentForm.codigoGuia = `${prefix}${timestamp}${random}`;
  }

  // Guardar dirección como frecuente
  saveAsFrequentAddress(): void {
    const dest = this.shipmentForm.destinatario;
    const frequentAddress = {
      name: dest.nombre,
      email: dest.email,
      phone: dest.telefono,
      reference: dest.referencia,
      department: dest.departamento,
      municipality: dest.municipio,
      poblado: dest.poblado,
      colonia: dest.colonia,
      exactAddress: dest.direccionExacta,
      contactName: dest.nombreContacto,
      specialInstructions: dest.instruccionesEspeciales
    };
    
    this.subscription.add(
      this.http.post(`${this.apiUrl}/frequent-addresses`, frequentAddress).subscribe({
        next: (response: any) => {
          if (response.success) {
            this.successMessage = 'Dirección guardada como frecuente';
            this.loadFrequentAddresses(); // Recargar lista
          }
        },
        error: (error) => {
          console.error('Error saving frequent address:', error);
        }
      })
    );
  }

  // Envío final del formulario
  onSubmit(): void {
    if (!this.validateCurrentStep()) {
      return;
    }
    
    this.isSubmitting = true;
    this.clearMessages();
    
    const shipmentData = {
      destinatario: this.shipmentForm.destinatario,
      metodoPago: this.shipmentForm.metodoPago,
      paquete: this.shipmentForm.paquete,
      costoTotal: this.shipmentForm.costoTotal,
      codigoGuia: this.shipmentForm.codigoGuia,
      createdBy: this.currentUser?.id
    };
    
    this.subscription.add(
      this.http.post(`${this.apiUrl}/shipments`, shipmentData).subscribe({
        next: (response: any) => {
          this.isSubmitting = false;
          
          if (response.success) {
            this.successMessage = `¡Envío creado exitosamente! Código de guía: ${this.shipmentForm.codigoGuia}`;
            
            // Generar PDF automáticamente
            this.generatePDF();
            
            setTimeout(() => {
              this.router.navigate(['/rastreo'], { 
                queryParams: { codigo: this.shipmentForm.codigoGuia } 
              });
            }, 3000);
          } else {
            this.errorMessage = response.message || 'Error al crear el envío';
          }
        },
        error: (error: HttpErrorResponse) => {
          this.isSubmitting = false;
          this.errorMessage = 'Error interno del servidor. Intenta nuevamente.';
          console.error('Error creating shipment:', error);
        }
      })
    );
  }

  // Generar PDF de la guía
  generatePDF(): void {
    // Esta función se implementará para generar el PDF con todos los datos
    console.log('Generando PDF para guía:', this.shipmentForm.codigoGuia);
    
    // Aquí se implementaría la lógica para generar el PDF
    // Puede usar librerías como jsPDF o llamar a un endpoint del backend
  }

  // Utilidades
  private clearMessages(): void {
    this.errorMessage = '';
    this.successMessage = '';
  }

  resetForm(): void {
    this.currentStep = 1;
    this.shipmentForm = {
      destinatario: {
        nombre: '', email: '', referencia: '', poblado: '', municipio: '', 
        departamento: '', colonia: '', residenciaBarrio: '', telefono: '', 
        instruccionesEspeciales: '', direccionExacta: '', nombreContacto: ''
      },
      metodoPago: { tipo: 'contra_entrega', cargoAdicional: 4.00 },
      paquete: {
        tipo: '', peso: 0, dimensiones: { largo: 0, ancho: 0, alto: 0 },
        descripcion: '', valorDeclarado: 0, fragil: false
      },
      costoTotal: 0, codigoGuia: ''
    };
    this.clearMessages();
  }

  getStepTitle(step: number): string {
    const titles = {
      1: 'Información del Destinatario',
      2: 'Método de Pago',
      3: 'Detalles del Paquete',
      4: 'Resumen y Confirmación'
    };
    return titles[step as keyof typeof titles] || 'Paso';
  }

  // Función para obtener el precio máximo permitido por tipo de paquete
  getMaxWeightForPackageType(tipo: string): number {
    const paquete = this.tiposPaquete.find(p => p.value === tipo);
    return paquete ? paquete.maxPeso : 0;
  }

  // Función para validar que el peso no exceda el máximo
  validateWeight(): boolean {
    const maxWeight = this.getMaxWeightForPackageType(this.shipmentForm.paquete.tipo);
    return this.shipmentForm.paquete.peso <= maxWeight;
  }
}
