  // Filtrar direcciones frecuentes
  getFilteredFrequentAddresses() {
    if (!this.searchTerm) return this.frequentAddresses;
    const term = this.searchTerm.toLowerCase();
    return this.frequentAddresses.filter(addr => addr.nickname.toLowerCase().includes(term) || addr.contactName.toLowerCase().includes(term) || addr.address.department.toLowerCase().includes(term) || addr.address.municipality.toLowerCase().includes(term));
  }
  // Seleccionar dirección frecuente
