        icon: '👤',
        route: '/admin/profile',
        description: 'Información personal'
      }, {
        title: 'Cotizar',
        icon: '💰',
        route: '/cotizar',
        description: 'Realizar cotización'
      }, {
        title: 'Nuevo Envío',
        icon: '📋',
        route: '/realizar-envio',
        description: 'Crear un nuevo envío'
      }, {
        title: 'Mis Envíos',
        icon: '📦',
        route: '/admin/my-shipments',
        description: 'Mis envíos'
