  loadDashboardStats() {
    // Simular carga de datos
    setTimeout(() => {
      this.stats = {
        totalQuotes: 1247,
        totalShipments: 892,
        pendingShipments: 34,
        completedShipments: 858
      };
      this.isLoading = false;
    }, 1000);
  }
