import { Component, OnInit, OnDestroy } from '@angular/core';
import { Router } from '@angular/router';
import { Subscription } from 'rxjs';
import { AuthService } from '../../services/auth.service';
import { DatabaseService, TableInfo, DatabaseStatus } from '../../services/database.service';

interface User {
  id: string;
  username: string;
  name: string;
  role: string;
  email: string;
}

interface DashboardStats {
  totalQuotes: number;
  totalShipments: number;
  pendingShipments: number;
  completedShipments: number;
}

export interface NewShipmentForm {
  receiverName: string;           // ✅
  receiverEmail: string;          // ✅
  receiverReference: 'casa' | 'trabajo' | 'gimnasio' | 'escuela';  // ✅
  receiverDepartamento: string;   // ✅
  receiverMunicipio: string;      // ✅
  receiverPoblado: string;        // ✅
  receiverPhone?: string;         // ✅
  paymentMethodId: string;        // ✅
  packageTypeId: string;          // ✅
  // ... otras propiedades
}

@Component({
  selector: 'app-admin',
  templateUrl: './admin.component.html',
  styleUrls: ['./admin.component.css']
})
export class AdminComponent implements OnInit, OnDestroy {
  private subscription: Subscription = new Subscription();
  
  currentUser: User | null = null;
  stats: DashboardStats = {
    totalQuotes: 0,
    totalShipments: 0,
    pendingShipments: 0,
    completedShipments: 0
  };
  
  isLoading = true;
  currentTime = new Date();
  
  // Menú de navegación según el rol
  menuItems: any[] = [];

  // Gestión de Base de Datos
  showDatabasePanel = false;
  dbStatus: DatabaseStatus | null = null;
  dbTables: TableInfo[] = [];
  selectedTable: TableInfo | null = null;
  tableData: any[] = [];
  currentPage = 0;
  pageSize = 20;
  totalRows = 0;
  customQuery = '';
  queryResults: any[] = [];
  loadingDB = false;

  constructor(
    private authService: AuthService,
    private router: Router,
    private databaseService: DatabaseService
  ) {}

  ngOnInit(): void {
    // Actualizar hora cada segundo
    setInterval(() => {
      this.currentTime = new Date();
    }, 1000);

    // Cargar datos del usuario
    this.loadUserData();
    
    // Simular carga de estadísticas
    this.loadDashboardStats();
  }

  ngOnDestroy(): void {
    this.subscription.unsubscribe();
  }

  private loadUserData(): void {
    this.subscription.add(
      this.authService.currentUser$.subscribe(user => {
        this.currentUser = user;
        this.setupMenuItems();
      })
    );
  }

  private setupMenuItems(): void {
    if (!this.currentUser) return;

    const baseItems = [
      {
        title: 'Dashboard',
        icon: '🏠',
        route: '/admin',
        description: 'Panel principal'
      },
      {
        title: 'Cotizar',
        icon: '💰',
        route: '/cotizar',
        description: 'Realizar cotización'
      },
      {
        title: 'Nuevo Envío',
        icon: '📋',
        route: '/realizar-envio',
        description: 'Crear un nuevo envío'
      }
    ];

    if (this.currentUser.role === 'admin') {
      this.menuItems = [
        ...baseItems,
        {
          title: 'Gestionar Cotizaciones',
          icon: '📊',
          route: '/admin/quotes',
